                    }
                }



var sortedOrders =
            db.Orders.Skip(0).Take(5).ToList().ThenBy(o => o, new
            OrdersComparer());

            Generates an ThenByDescending query for the IComparer defined. 




               public class OrdersComparer :
            IComparer<Order>
                {
